import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {

  constructor() {
    super();
    this.state={
      articles: [],
      loading: false,
      page: 1,
    }
  }

  async componentDidMount(){
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=5ead55ce73f7418286f82e9a2615e197&page=1&pageSize=20"
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
    });
  }

  handlePrevClick = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=5ead55ce73f7418286f82e9a2615e197&page=${this.state.page-1}&pageSize=20`
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      page: this.state.page-1,
      articles: parsedData.articles
    })
  }

  handleNextClick = async ()=>{
    if(this.state.page+1 > Math.ceil(this.state.totalResults/20)){

    }
    else{
      let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=5ead55ce73f7418286f82e9a2615e197&page=${this.state.page+1}&pageSize=20`
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({
        page: this.state.page+1,
        articles: parsedData.articles
      })
    }
  }

  render() {
    return (
      <div className="container my-3">
        <h2>Cheems News - Top Headlines</h2>
        <div className="row">
        {this.state.articles.map((element)=>{
          return(<div className="col-md-4" key={element.url}>
            <NewsItem
              title={element.title?element.title.slice(0,45)+"...":"..."}
              description={element.description?element.title.slice(0,80)+"...":"..."}
              imageURL={element.urlToImage}
              newsURL={element.url}
            />
          </div>)
        })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1} type="button" className="btn btn-primary" onClick={this.handlePrevClick}>
            &larr; Previous
          </button>
          <button type="button" className="btn btn-primary" onClick={this.handleNextClick}>
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
