import React, { Component } from "react";

export class NewsItem extends Component {
  render() {
    let {title,description,imageURL,newsURL,author,date} = this.props;
    return (
      <div>
        <div className="card my-3 mx-5"> 
          <img src={imageURL} className="card-img-top" alt="Not Found" />
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <p className="card-text">
              {description}
            </p>
            <a rel="noreferer" href={newsURL} target="_blank" className="btn btn-sm btn-primary">
              Read More
            </a>
            <p className="card-text"><small className="text-muted">By {!author?"Unknown":author} on {new Date(date).toG}</small></p>
          </div>
        </div>
      </div>
    );
  }
}

export default NewsItem;
